import React, { useState } from "react";
import "./Expense.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

function Expense(props) {
  const [datafromFilter, setdatafromFilter] = useState("2019");

  const onselectdataHandler = (selecteddata) => {
    setdatafromFilter(selecteddata);
  };

  function checkYear(reqYear) {
    const dateToCheck = new Date(props.date);
    return reqYear === dateToCheck.getFullYear();
  }

  let filteredExpenses = props.expense.filter(checkYear(props.date));

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          ondatafromFilter={datafromFilter}
          onselectdata={onselectdataHandler}
        />
        {filteredExpenses.map((expense) => (
          <ExpenseItem
            key={filteredExpenses.id}
            title={filteredExpenses.title}
            amount={filteredExpenses.amount}
            date={filteredExpenses.date}
          />
        ))}
      </Card>
    </div>
  );
}

export default Expense;
