import React, { useState } from "react";
import "./LoginForm.css";

const LoginForm = (props) => {
  const [token, settoken] = useState("");

  const [userinput, setuserinput] = useState({
    username: "",
    password: "",
  });

  const onusernamechangeHandler = (event) => {
    setuserinput((pervalue) => {
      return {
        ...pervalue,
        username: event.target.value,
      };
    });
  };
  const onpasswordchangeHandler = (event) => {
    setuserinput((pervalue) => {
      return {
        ...pervalue,
        password: event.target.value,
      };
    });
  };

  const loginreq = (userinput) => {
    fetch("http://localhost:1000/api/auth/signin", {
      method: "POST",
      body: JSON.stringify(userinput),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((res) => {
        settoken(res.token);
      });
  };

  const setInputnull = () => {
    setuserinput({
      username: "",
      password: "",
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    setuserinput({
      username: event.target.value.username,
      password: event.target.value.password,
    });
    loginreq(userinput);
    props.valid(token);
    setInputnull();
  };

  return (
    <form onSubmit={submitHandler} className="loginform">
      <div className="login__control">
        <div className="holderl">
          <label for="username">Username</label>
        </div>
        <div className="holderin">
          <input
            type="text"
            id="username"
            value={userinput.username}
            onChange={onusernamechangeHandler}
          />
        </div>
      </div>
      <div className="login__control">
        <div className="holderl">
          <label for="password">Password</label>
        </div>
        <div className="holderin">
          <input
            type="text"
            min="1"
            id="password"
            value={userinput.password}
            onChange={onpasswordchangeHandler}
          />
        </div>
      </div>

      <div className="login__actions">
        <button type="button" onClick={setInputnull}>
          Cancel
        </button>
        <button type="submit">Login</button>
      </div>
    </form>
  );
};

export default LoginForm;
