import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [userinput, setuserinput] = useState({
    summary: "",
    amount: "",
    type: "",
    date: new Date(),
  });

  const onsummarychangeHandler = (event) => {
    setuserinput((pervalue) => {
      return {
        ...pervalue,
        summary: event.target.value,
      };
    });
  };
  const onamountchangeHandler = (event) => {
    setuserinput((pervalue) => {
      return {
        ...pervalue,
        amount: event.target.value,
      };
    });
  };
  const ondatechangeHandler = (event) => {
    setuserinput((pervalue) => {
      return {
        ...pervalue,
        date: event.target.valueAsDate,
      };
    });
  };
  const ontypechangeHandler = (event) => {
    setuserinput((pervalue) => {
      return {
        ...pervalue,
        type: event.target.value,
      };
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    setuserinput({
      summary: event.target.title.value,
      amount: +event.target.amount.value,
      date: event.target.date.valueAsDate,
    });
    props.onSaveExpenseData(userinput);
    setuserinput({
      summary: "",
      amount: "",
      type: "",
      date: new Date(),
    });
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__controls">
          <label>Summary</label>
          <input
            type="text"
            id="summary"
            value={userinput.title}
            onChange={onsummarychangeHandler}
          />
        </div>
        <div className="new-expense__controls">
          <label>Amount</label>
          <input
            type="number"
            min="1"
            id="amount"
            value={userinput.amount}
            onChange={onamountchangeHandler}
          />
        </div>
        <div className="new-expense__controls">
          <label>Type</label>
          <select value={userinput.type} onChange={ontypechangeHandler}>
            <option value={"credit"}>Credit</option>
            <option value={"debit"}>Debit</option>
          </select>
        </div>
        <div className="new-expense__controls">
          <label>Date</label>
          <input
            type="Date"
            min="2019-01-01"
            max="2022-12-31"
            id="date"
            onChange={ondatechangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="button" onClick={props.oncancel}>
          Cancel
        </button>
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
