  //             });
  //             return 0;
  //           });
  //           return prev;
  //         });
  //       },
  //       (err) => {
  //         console.log("err");
  //       }
  //     );
  // }, []);

  // console.log(gete);
  // console.log(expense);

  async function postList(expenseadd) {
    try {
      const response = await fetch("http://localhost:1000/api/expense/add", {
        method: "POST",
        body: JSON.stringify(expenseadd),
        headers: {
          "Content-Type": "application/json",
          token: localStorage.getItem("token"),
        },
      });
      if (!response.ok) {
        const res = await response.json();
        alert(res.message);
      } else {
        const res = await response.json();
        console.log(res);
        getalldata(localStorage.getItem("token"));
      }
    } catch (error) {
      alert("Something went wrong!");
    }
  }

  const addExpenseHandler = (expenseadd) => {
    // setExpense((preExpense) => {
    //   return [expenseadd, ...preExpense];
    // });

    postList(expenseadd);
  };

  return (
    <div>
      {valid && <LogoutBar logoutit={setvalidfalse} />}
      {valid && <NewExpense onAddExpense={addExpenseHandler} />}
      {valid && <Expense expense={expense} />}
      {!valid && <LoginPage validit={setvalidtrue} />}
    </div>
  );
}

export default App;
