import React, { useState } from "react";
import Expense from "./components/Expenses/Expense";
import NewExpense from "./components/NewExpense/NewExpense";
import LoginPage from "./components/Home/LoginPage";
import LogoutBar from "./components/Home/LogoutBar";

function App() {
  // const initialExpense = [
  //   {
  //     id: "e1",
  //     title: "Toilet Paper",
  //     amount: 94.12,
  //     date: new Date(2020, 7, 14),
  //   },
  //   { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  //   {
  //     id: "e3",
  //     title: "Car Insurance",
  //     amount: 294.67,
  //     date: new Date(2021, 2, 28),
  //   },
  //   {
  //     id: "e4",
  //     title: "New Desk (Wooden)",
  //     amount: 450,
  //     date: new Date(2021, 5, 12),
  //   },
  // ];
  const [valid, setValid] = useState(false);
  const [expense, setExpense] = useState([]);
  const [profile, setprofile] = useState([]);

  async function getalldata(tokenin) {
    try {
      const response = await fetch("http://localhost:1000/api/expense/all", {
        headers: {
          token: tokenin,
        },
      });
      if (!response.ok) {
        alert("Something went wrong");
      } else {
        const res = await response.json();
        setExpense((prev) => {
          prev = [];
          res.map((item) => {
            prev.push({
              amount: item.amount.$numberDecimal,
              summary: item.summary,
              expensetype: item.expensetype,
              date: new Date(),
            });
            return 0;
          });
          return prev;
        });
        setValid(true);
      }
    } catch (error) {
      alert("Something went wrong");
    }
  }

  async function getprofile(token) {
    try {
      const response = await fetch("http://localhost:1000/api/profile", {
        headers: {
          token: token,
        },
      });
      if (!response.ok) {
        alert("Something is wrong");
      } else {
        const res = await response.json();
        setprofile(res);
      }
    } catch (error) {
      alert(error);
    }
  }

  const setvalidtrue = (token) => {
    getalldata(token);
    getprofile(token);
  };

  const setvalidfalse = () => {
    localStorage.removeItem("token");
    setValid(false);
  };

  async function postList(expenseadd) {
    try {
      const response = await fetch("http://localhost:1000/api/expense/add", {
        method: "POST",
        body: JSON.stringify(expenseadd),
        headers: {
          "Content-Type": "application/json",
          token: localStorage.getItem("token"),
        },
      });
      if (!response.ok) {
        const res = await response.json();
        alert(res.message);
      } else {
        const res = await response.json();
        alert(res.message);
        getalldata(localStorage.getItem("token"));
        getprofile(localStorage.getItem("token"));
      }
    } catch (error) {
      alert("Something went wrong!");
    }
  }

  const addExpenseHandler = (expenseadd) => {
    // setExpense((preExpense) => {
    //   return [expenseadd, ...preExpense];
    // });

    postList(expenseadd);
  };

  return (
    <div>
      {valid && <LogoutBar logoutit={setvalidfalse} />}
      {valid && <NewExpense onAddExpense={addExpenseHandler} />}
      {valid && (
        <Expense
          expense={expense}
          totalbal={profile.currentBalance.$numberDecimal}
        />
      )}
      {!valid && <LoginPage validit={setvalidtrue} />}
    </div>
  );
}

export default App;
