import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [needForm, setNeedForm] = useState(false);

  const [userinput, setuserinput] = useState({
    title: "",
    amount: "",
    date: "",
  });

  const ontitlechangeHandler = (event) => {
    setuserinput((pervalue) => {
      return {
        ...pervalue,
        title: event.target.value,
      };
    });
  };
  const onamountchangeHandler = (event) => {
    setuserinput((pervalue) => {
      return {
        ...pervalue,
        amount: event.target.value,
      };
    });
  };
  const ondatechangeHandler = (event) => {
    setuserinput((pervalue) => {
      return {
        ...pervalue,
        date: event.target.value,
      };
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    setuserinput({
      title: event.target.title.value,
      amount: event.target.amount.value,
      date: event.target.date.value,
    });
    props.onSaveExpenseData(userinput);

    setuserinput({
      title: "",
      amount: "",
      date: "",
    });
  };

  const setformconditionfalse = () => {
    setNeedForm(false);
  };
  const setformconditiontrue = () => {
    setNeedForm(true);
  };

  if (needForm) {
    return (
      <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
          <div className="new-expense__controls">
            <label>Title</label>
            <input
              type="text"
              id="title"
              value={userinput.title}
              onChange={ontitlechangeHandler}
            />
          </div>
          <div className="new-expense__controls">
            <label>Amount</label>
            <input
              type="number"
              min="1"
              step="1"
              id="amount"
              value={userinput.amount}
              onChange={onamountchangeHandler}
            />
          </div>
          <div className="new-expense__controls">
            <label>Date</label>
            <input
              type="Date"
              min="2019-01-01"
              max="2022-12-31"
              id="date"
              value={userinput.date}
              onChange={ondatechangeHandler}
            />
          </div>
        </div>
        <div className="new-expense__actions">
          <button onClick={setformconditionfalse}>Cancle</button>
          <button type="submit">Add Expense</button>
        </div>
      </form>
    );
  } else {
    return (
      <div className="new-expense__openForm">
        <div className="new-expense__actions">
          <button onClick={setformconditiontrue}>Add Expenses</button>
        </div>
      </div>
    );
  }
};

export default ExpenseForm;
