import React, { useState } from "react";
import "./LoginForm.css";

const LoginForm = (props) => {
  const [errmsg, seterrmsg] = useState("");
  const [userinput, setUserinput] = useState({
    email: "",
    password: "",
  });

  const onemailchangeHandler = (event) => {
    setUserinput((pervalue) => {
      return {
        ...pervalue,
        email: event.target.value,
      };
    });
  };
  const onpasswordchangeHandler = (event) => {
    setUserinput((pervalue) => {
      return {
        ...pervalue,
        password: event.target.value,
      };
    });
  };

  async function loginreq(userinput) {
    try {
      const response = await fetch("http://localhost:1000/api/auth/signin", {
        method: "POST",
        body: JSON.stringify(userinput),
        headers: {
          "Content-Type": "application/json",
        },
      });
      if (!response.ok) {
        // throw Error(response.statusText);
        seterrmsg(response.statusText);
      } else {
        const res = await response.json();
        localStorage.setItem("token", res.token);
        props.valid(localStorage.getItem("token"));
        seterrmsg("");
      }
    } catch (error) {
      console.log(error);
    }
  }

  const setInputnull = () => {
    setUserinput({
      email: "",
      password: "",
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    setUserinput({
      email: event.target.email.value,
      password: event.target.password.value,
    });
    loginreq(userinput);
    console.log(localStorage.getItem("token"));
    setInputnull();
  };

  return (
    <form onSubmit={submitHandler} className="loginform">
      <div className="login__control">
        <div className="holderl">
          <label>Email</label>
        </div>
        <div className="holderin">
          <input
            type="text"
            id="email"
            value={userinput.email}
            onChange={onemailchangeHandler}
          />
        </div>
      </div>
      <div className="login__control">
        <div className="holderl">
          <label>Password</label>
        </div>
        <div className="holderin">
          <input
            type="password"
            min="6"
            id="password"
            value={userinput.password}
            onChange={onpasswordchangeHandler}
          />
        </div>
        <div className="login__control">
          <div className="holderin">
            <label>{errmsg}</label>
          </div>
        </div>
      </div>

      <div className="login__actions">
        <button type="button" onClick={setInputnull}>
          Cancel
        </button>
        <button type="submit">Login</button>
      </div>
    </form>
  );
};

export default LoginForm;
