  //     date: new Date(2021, 5, 12),
  //   },
  // ];
  const [valid, setValid] = useState(false);
  const [expense, setExpense] = useState([]);

  const getalldata = (token) => {
    fetch("http://localhost:1000/api/expense/all", {
      headers: {
        token: token,
      },
    })
      .then((res) => res.json())
      .then(
        (res) => {
          console.log(res);
          setExpense((prev) => {
            res.map((item) => {
              prev.push({
                amount: item.amount.$numberDecimal,
                summary: item.summary,
                expensetype: item.type,
                date: new Date(),
              });
              return 0;
            });
            return prev;
          });
          setValid(true);
        },
        (err) => {
          console.log(err);
        }
      );
  };

  const setvalidtrue = (token) => {
    console.log(token);
    getalldata(token);
  };

  const setvalidfalse = () => {
    setValid(false);
  };

  // useEffect(() => {
  //   fetch("http://localhost:1000/Expense/")
  //     .then((res) => res.json())
  //     .then(
  //       (res) => {
  //         setExpense((prev) => {
  //           res.map((item) => {
  //             prev.push({
  //               id: item.id,
  //               title: item.title,
  //               amount: item.amount,
  //               date: new Date(item.date),
  //             });
  //             return 0;
  //           });
  //           return prev;
  //         });
  //       },
  //       (err) => {
  //         console.log("err");
  //       }
  //     );
  // }, []);

  // console.log(gete);
  // console.log(expense);

  function postList(expenseadd) {
    fetch("http://localhost:5000/Expense/", {
      method: "POST",
      body: JSON.stringify(expenseadd),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then(
        (res) => {
          const expenseadded = {
            id: res.id,
            title: res.title,
            amount: res.amount,
            date: new Date(res.date),
          };
          setExpense((preExpense) => {
            return [expenseadded, ...preExpense];
          });
        },
        (err) => {
          console.log("err in post");
        }
      );

    console.log(JSON.stringify(expenseadd));
  }

  const addExpenseHandler = (expenseadd) => {
    // setExpense((preExpense) => {
    //   return [expenseadd, ...preExpense];
    // });

    postList(expenseadd);
  };

  return (
    <div>
      {valid && <LogoutBar logoutit={setvalidfalse} />}
      {valid && <NewExpense onAddExpense={addExpenseHandler} />}
      {valid && <Expense expense={expense} />}
      {!valid && <LoginPage validit={setvalidtrue} />}
    </div>
  );
}

export default App;
